import { Component } from '@angular/core';

@Component({
  selector: 'happy-habit2-root',
  template: `
    <div class="container">
      <div *ngFor="let i of [0, 1, 2, 3]" class="row">
        <div *ngFor="let j of [1, 2, 3, 4]" class="col-sm mt-4">
          <img
            [src]="'assets/negative1/' + (+i + +j+1) + '.jpeg'"
            class="img-fluid"
            [alt]="'Responsive image' + (+i + +j+1)"
          />
        </div>
      </div>
    </div>
  `,
})
export class AppComponent {
  title = 'happy-habit2';

  constructor() {
    const array = [];

    // generate 65 random numbers between 0 and 16
    for (let i = 0; i < 64; i++) {
      const min = 0;
      const max = 16;
      array.push(Math.floor(Math.random() * (max - min + 1) + min));
    }

    const uniqueArray = [...new Set(array)];
    // get only the first elements
    const uniqueArray2 = uniqueArray.splice(0, 16);
  }
}
